record.field:
def get_number_code(default_code):
    try:
        number_part = int(''.join(filter(str.isdigit, default_code)))
    except:
        number_part = 1

    char_part = ''.join(filter(lambda x: not x.isdigit(), default_code))
    vals = [char_part,number_part]
    return vals

def get_largest_code():
    rec = env['product.product'].search([('default_code','!=',False)], order='default_code desc', limit=1)
    if rec:
      return get_number_code(rec.default_code)

if record:
    if record.default_code:
        pass
    else:
        current_largest_code = get_largest_code()
        record.write({
            'default_code': current_largest_code[0] + str(current_largest_code[1] + 1)
        })
